package leetcode;import java.util.Scanner;/** * Created by lixiaoyan on 2017/3/31. */public class ListNode {    int val;    ListNode next;    ListNode(int x){        val = x;    }    public static ListNode init(){        ListNode head = new ListNode(-1);        ListNode p = head;        Scanner scanner = new Scanner(System.in);        int num = scanner.nextInt();        while (num > 0){         int x = scanner.nextInt();         ListNode temp = new ListNode(x);         p.next = temp;         p = p.next;         num--;        }        return  head.next;    }    public void showNode(ListNode head){        ListNode p = head;        while(p != null){            System.out.print(p.val+"->");            p = p.next;        }    }}